mapper.select:
SELECT n.NEWS_ID, n.TITLE, n.DATE, n.BODY, n.BRAND, n.CATEGORY, k.KEYWORD, n.URL, n.BODYTEXT, n.THUMB FROM news n JOIN newskeyword k WHERE n.NEWS_ID = k.NEWS_ID AND (
(
n.TITLE LIKE CONCAT('%',#{word},'%')
)
OR
(
k.KEYWORD LIKE CONCAT('%',#{word},'%')
)
) ORDER BY DATE DESC

SELECT n.NEWS_ID, n.TITLE, n.DATE, n.BODY, n.BRAND, n.CATEGORY, k.KEYWORD, n.URL, n.BODYTEXT, n.THUMB FROM news n JOIN newskeyword k WHERE n.NEWS_ID = k.NEWS_ID AND BRAND LIKE #{brand} AND (
(
n.TITLE LIKE CONCAT('%',#{word},'%')
)
OR
(
k.KEYWORD LIKE CONCAT('%',#{word},'%')
)
) ORDER BY DATE DESC

