SELECT n.NEWS_ID, n.TITLE, n.DATE, n.BODY, n.BRAND, n.CATEGORY, k.KEYWORD, n.URL, n.BODYTEXT, n.THUMB FROM news n JOIN newskeyword k WHERE n.NEWS_ID = k.NEWS_ID AND (
(
k.KEYWORD LIKE CONCAT('%',#{word},'%')
)
) AND n.DATE >= DATE_SUB(NOW(), INTERVAL 31 DAY) ORDER BY n.DATE DESC
